// src/components/MessageBubble.tsx

import React from 'react';
import { Message } from '../types'; // Import the Message interface
import QuoteDisplay from '../components/QuoteDisplay'; // Import the QuoteDisplay component for nested rendering

interface MessageBubbleProps {
  message: Message; // The message object to display
}

const MessageBubble: React.FC<MessageBubbleProps> = ({ message }) => {
  const isAI = message.sender === 'AI'; // Check if the message is from the AI

  return (
    <div style={{
      display: 'flex',
      justifyContent: isAI ? 'flex-start' : 'flex-end', // Align left for AI, right for You
      margin: '10px 0',
      padding: '0 15px' // Horizontal padding for the bubbles
    }}>
      <div style={{
        maxWidth: '70%', // Limit bubble width
        padding: '12px 18px',
        borderRadius: '20px', // Rounded corners for chat bubbles
        backgroundColor: isAI ? '#e0e0e0' : '#d1e7dd', // Light gray for AI, light green for You
        color: '#333', // Dark text color
        wordWrap: 'break-word', // Ensure long words wrap
        boxShadow: '0 1px 2px rgba(0,0,0,0.1)' // Subtle shadow
      }}>
        <strong style={{ color: isAI ? '#3f51b5' : '#007bff' }}>{message.sender}:</strong>
        {message.text && <p style={{ margin: '5px 0 0 0' }}>{message.text}</p>} {/* Display text if present */}
        {message.quoteDetails && <QuoteDisplay {...message.quoteDetails} />} {/* Render QuoteDisplay if quote details are present */}
      </div>
    </div>
  );
};

export default MessageBubble;
