// src/components/QuoteDisplay.tsx

import React from 'react';
import { QuoteDisplayProps, QuoteItem } from '../types'; // Import necessary interfaces

const QuoteDisplay: React.FC<QuoteDisplayProps> = ({
  quotationNumber,
  isRevision,
  status,
  date,
  totalAmount,
  items,
  leadInfo
}) => {
  return (
    <div style={{
      border: '1px solid #ccc', // Light gray border
      borderRadius: '8px', // Slightly rounded corners
      padding: '15px',
      marginTop: '10px',
      backgroundColor: '#f9f9f9', // Very light gray background
      fontSize: '0.9em' // Slightly smaller font size for details
    }}>
      <p style={{ margin: '0 0 10px 0', fontWeight: 'bold' }}>
        Quotation: {quotationNumber} {isRevision && "(Revision)"} {/* Display revision if true */}
      </p>
      {leadInfo && <p style={{ margin: '5px 0' }}>{leadInfo}</p>} {/* Display lead info if present */}
      <ul>
        <li style={{ listStyle: 'none', marginBottom: '5px' }}>
          • Status: <span style={{ fontWeight: 'bold' }}>{status}</span>
        </li>
        <li style={{ listStyle: 'none', marginBottom: '5px' }}>
          • Date: {date}
        </li>
        <li style={{ listStyle: 'none', marginBottom: '10px' }}>
          • Total Amount: {totalAmount.toFixed(2)} {/* Format total amount to 2 decimal places */}
        </li>
      </ul>
      <p style={{ margin: '0 0 5px 0', fontWeight: 'bold' }}>• Items:</p>
      <ul style={{ paddingLeft: '20px', margin: 0 }}> {/* Indent for item list */}
        {items.map((item, index) => (
          <li key={index} style={{ listStyle: 'disc', marginBottom: '3px' }}>
            Item ID {item.itemID}: {item.units} units @ {item.unitPrice.toFixed(2)} each (Total: {item.total.toFixed(2)})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default QuoteDisplay;
