// src/components/UserInput.tsx

import React, { useState } from 'react';

interface UserInputProps {
  onSendMessage: (message: string) => void; // Callback function when a message is sent
}

const UserInput: React.FC<UserInputProps> = ({ onSendMessage }) => {
  const [message, setMessage] = useState(''); // State to hold the current input message

  const handleSend = () => {
    if (message.trim()) { // Only send if message is not empty or just whitespace
      onSendMessage(message); // Call the parent's send function
      setMessage(''); // Clear the input field
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') { // Send message on Enter key press
      handleSend();
    }
  };

  return (
    <div style={{
      display: 'flex',
      padding: '15px',
      borderTop: '1px solid #ddd', // Separator line at the top
      borderRadius: '0 0 8px 8px', // Rounded bottom corners
      backgroundColor: '#f5f5f5' // Light background
    }}>
      <input
        type="text"
        placeholder="Type your message..."
        value={message}
        onChange={(e) => setMessage(e.target.value)} // Update message state on input change
        onKeyPress={handleKeyPress} // Handle Enter key
        style={{
          flexGrow: 1, // Allows input to take available space
          padding: '10px 15px',
          border: '1px solid #ccc',
          borderRadius: '20px', // Pill-shaped input
          marginRight: '10px',
          fontSize: '1em'
        }}
      />
      <button
        onClick={handleSend} // Handle click on Send button
        style={{
          backgroundColor: '#4CAF50', // Green send button color
          color: 'white',
          border: 'none',
          borderRadius: '20px', // Pill-shaped button
          padding: '10px 20px',
          cursor: 'pointer',
          fontSize: '1em',
          fontWeight: 'bold',
          transition: 'background-color 0.3s ease' // Smooth transition on hover/active
        }}
      >
        Send
      </button>
    </div>
  );
};

export default UserInput;
