// src/components/ChatContainer.tsx

import React, { useState, useEffect, useRef } from 'react';
import ChatHeader from './ChatHeader';
import MessageBubble from './MessageBubble';
import UserInput from './UserInput';
import { Message, QuoteDisplayProps } from '../types';
import { v4 as uuidv4 } from 'uuid';

// Define the API endpoint for your backend
const API_URL = 'http://localhost:8000/query'; // Make sure this matches your FastAPI server address and port

const ChatContainer: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: uuidv4(),
      sender: 'AI',
      text: 'Hello! I am your AI CRM Assistant. How can I help you today?',
    },
  ]);
  const [isLoading, setIsLoading] = useState<boolean>(false); // State for loading indicator
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (text: string) => {
    // Add user's message to chat
    const newUserMessage: Message = {
      id: uuidv4(),
      sender: 'You',
      text: text,
    };
    setMessages((prevMessages) => [...prevMessages, newUserMessage]);
    setIsLoading(true); // Show loading indicator

    try {
      // Make the API call to your FastAPI backend
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: text,
          include_sql: true, // Request SQL for debugging/display if needed
          include_results: true, // Request raw SQL results
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to get response from API.');
      }

      const data = await response.json();
      console.log("API Response:", data); // Log the full API response for debugging

      let aiMessageContent: Partial<Message> = {};

      if (data.success) {
        aiMessageContent.text = data.response;

        // Check for structured quote details in the response
        if (data.sql_results && data.sql_results.length > 0) {
            // Attempt to parse the SQL results into QuoteDisplayProps format
            // This part is a heuristic and needs to match how your backend structures quote data.
            // A more robust solution involves your backend specifically returning a 'type' like 'quote'
            // and a 'payload' matching QuoteDisplayProps directly.

            // For now, let's assume if there are results and the user asked about 'quote' or 'quotation'
            // and the response text might imply quote data, we can try to format it.
            const userQueryLower = text.toLowerCase();
            const isQuoteQuery = userQueryLower.includes("quote") || userQueryLower.includes("quotation");

            if (isQuoteQuery && data.sql_results[0]) {
                const firstResult = data.sql_results[0];
                try {
                    const parsedQuote: QuoteDisplayProps = {
                        quotationNumber: String(firstResult.quotation_id || firstResult.id || 'N/A'),
                        status: String(firstResult.status || 'N/A'),
                        date: String(firstResult.date || firstResult.order_date || 'N/A'),
                        totalAmount: parseFloat(firstResult.total_amount || firstResult.order_value || 0).toFixed(2) as unknown as number, // Cast to number after parsing
                        items: [], // Initialize empty; you'll need to populate this properly
                                  // if your SQL results don't provide itemized data directly in the main query.
                                  // For a robust solution, you might need a separate API call or a more complex agent for quote items.
                        leadInfo: String(firstResult.lead_name || firstResult.customer_name || 'N/A')
                    };
                    
                    // Simple example for items if your SQL returns them in a nested list/json or separate table
                    // You'll need to adapt this based on your actual SQL output
                    if (firstResult.items && Array.isArray(firstResult.items)) {
                        parsedQuote.items = firstResult.items.map((item: any) => ({
                            itemID: String(item.item_id || 'N/A'),
                            units: parseFloat(item.units || 0),
                            unitPrice: parseFloat(item.unit_price || 0),
                            total: parseFloat(item.total || 0)
                        }));
                    } else {
                        // If no specific item details, add a generic one
                        parsedQuote.items.push({ itemID: "N/A", units: 0, unitPrice: 0, total: 0 });
                    }

                    // Check for isRevision
                    if (firstResult.revision_number && firstResult.revision_number > 0) {
                        parsedQuote.isRevision = true;
                    }
                    
                    aiMessageContent.quoteDetails = parsedQuote;
                } catch (parseError) {
                    console.error("Error parsing quote details from API response:", parseError);
                    // Fallback to just text response if parsing fails
                    aiMessageContent.text += "\n(Note: Could not fully parse quote details for display.)";
                }
            }
        }
      } else {
        // If data.success is false, it means the backend reported an error
        aiMessageContent.text = data.error || "An error occurred on the server.";
        console.error("Backend Error:", data.error);
      }

      // Add AI's response to chat
      const newAiMessage: Message = {
        id: uuidv4(),
        sender: 'AI',
        ...aiMessageContent, // Spread the content (text and potentially quoteDetails)
      };
      setMessages((prevMessages) => [...prevMessages, newAiMessage]);

    } catch (error) {
      console.error('Error sending message to API:', error);
      const errorMessage: Message = {
        id: uuidv4(),
        sender: 'AI',
        text: `I'm sorry, I couldn't connect to the backend or process your request. Please try again. Error: ${error instanceof Error ? error.message : String(error)}`,
      };
      setMessages((prevMessages) => [...prevMessages, errorMessage]);
    } finally {
      setIsLoading(false); // Hide loading indicator
    }
  };

  return (
    <div style={{
      width: '100%',
      maxWidth: '750px',
      height: '85vh',
      margin: '20px auto',
      border: 'none',
      borderRadius: '8px',
      display: 'flex',
      flexDirection: 'column',
      boxShadow: '0 8px 25px rgba(0,0,0,0.25)',
      backgroundColor: 'white',
      overflow: 'hidden'
    }}>
      <ChatHeader title="AI CRM Assistant" />
      <div style={{
        flexGrow: 1,
        overflowY: 'auto',
        padding: '10px 0',
        backgroundColor: '#f8f9fa',
        borderBottom: '1px solid #e0e0e0'
      }}>
        {messages.map((msg) => (
          <MessageBubble key={msg.id} message={msg} />
        ))}
        {isLoading && (
          <div style={{
            display: 'flex',
            justifyContent: 'flex-start',
            margin: '8px 15px',
            opacity: 0.8
          }}>
            <div style={{
              backgroundColor: '#e9ecef',
              padding: '12px 16px',
              borderRadius: '18px',
              maxWidth: '75%',
              color: '#212529',
              fontStyle: 'italic'
            }}>
              AI is typing...
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <UserInput onSendMessage={handleSendMessage} />
    </div>
  );
};

export default ChatContainer;
